import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { ethers } from "ethers";
import { VotingAddress, VotingAbi } from "../context/constant";
import { Button } from "@mui/material";

function VotingPage() {
    const { id } = useParams();
    const [candidates, setCandidates] = useState([]);
    const [error, setError] = useState(null);
    const [canVote, setCanVote] = useState(true);
    const [electionStatus, setElectionStatus] = useState("");

    // Initialize provider and signer
    const initializeProvider = async () => {
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        await provider.send("eth_requestAccounts", []);
        const signer = provider.getSigner();
        const contract = new ethers.Contract(VotingAddress, VotingAbi, signer);

        return { provider, signer, contract };
    };

    // Fetch candidates for the current election
    const fetchCandidates = async () => {
        try {
            const { contract } = await initializeProvider();
            const fetchedCandidates = await contract.getCandidates(id);

            const formattedCandidates = fetchedCandidates.map((candidate) => ({
                name: candidate.name,
                voteCount: candidate.voteCount.toString(),
            }));

            setCandidates(formattedCandidates);
        } catch (err) {
            console.error("Error fetching candidates:", err);
            setError("Failed to load candidates.");
        }
    };

    // Check if the voter can vote
    const checkCanVote = async () => {
        try {
            const { signer, contract } = await initializeProvider();
            const voterAddress = await signer.getAddress();
            const hasVoted = await contract.hasVoted(id, voterAddress); // Ensure `hasVoted` is in the contract
            setCanVote(!hasVoted);
        } catch (err) {
            console.error("Error checking vote status:", err);
            setError("Failed to check vote status.");
        }
    };
    

    // Check election status
    const fetchElectionStatus = async () => {
        try {
            const { contract } = await initializeProvider();
            const election = await contract.getElection(id);
            const currentTime = Math.floor(Date.now() / 1000);

            console.log("Current Time:", currentTime);
            console.log("Election Start Time:", election.startTime);
            console.log("Election End Time:", election.endTime);

            if (currentTime < election.startTime) {
                setElectionStatus("Election has not started yet.");
                setCanVote(false);
            } else if (currentTime > election.endTime) {
                setElectionStatus("Election has ended.");
                setCanVote(false);
            } else {
                setElectionStatus("Election is ongoing.");
                setCanVote(true);
            }
        } catch (err) {
            console.error("Error fetching election status:", err);
            setError("Failed to fetch election status.");
        }
    };

    // Voting function
    const vote = async (candidateIndex) => {
    try {
        const { contract } = await initializeProvider();

        const tx = await contract.vote(id, candidateIndex, { gasLimit: 1000000 });
        await tx.wait();

        console.log(`Successfully voted for candidate index ${candidateIndex}.`);
        fetchElectionDetails(); // Refresh election details
        checkCanVote(); // Refresh voting status
    } catch (err) {
        console.error("Error during voting:", err);
        setError("Failed to submit your vote.");
    }
};

    const fetchElectionDetails = async () => {
        try {
            const { contract } = await initializeProvider();
    
            // Fetch election details
            const election = await contract.getElection(id);
            const fetchedCandidates = await contract.getCandidates(id);
    
            // Format candidates
            const formattedCandidates = fetchedCandidates.map((candidate) => ({
                name: candidate.name,
                voteCount: candidate.voteCount.toString(),
            }));
    
            setCandidates(formattedCandidates);
    
            // Handle election timing
            const currentTime = Math.floor(Date.now() / 1000);
            if (currentTime < election.startTime) {
                setElectionStatus("Election has not started yet.");
                setCanVote(false);
            } else if (currentTime > election.endTime) {
                setElectionStatus("Election has ended.");
                setCanVote(false);
            } else {
                setElectionStatus("Election is ongoing.");
                setCanVote(true);
            }
        } catch (err) {
            console.error("Error fetching election details:", err);
            setError("Failed to load election details.");
        }
    };
    
    useEffect(() => {
        fetchElectionDetails();
        checkCanVote();
    }, [id]);
    

    return (
        <div>
            <h1>Election {id}</h1>
            {error && <p style={{ color: "red" }}>{error}</p>}
            <p>{electionStatus}</p>
            {canVote ? (
                <ul>
                    {candidates.map((candidate, index) => (
                        <li key={index}>
                            {candidate.name}: {candidate.voteCount} votes
                            <Button
                                variant="contained"
                                color="primary"
                                onClick={() => vote(index)}
                                style={{ marginLeft: "10px" }}
                            >
                                Vote
                            </Button>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>You cannot vote in this election.</p>
            )}
        </div>
    );
}

export default VotingPage;
