import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { ethers } from "ethers";
import { VotingAddress, VotingAbi } from "../context/constant";
import { Button } from "@mui/material";

function VotingPage() {
    const { id } = useParams();
    const [candidates, setCandidates] = useState([]);
    const [error, setError] = useState(null);
    const [canVote, setCanVote] = useState(false); 
    const [electionStatus, setElectionStatus] = useState("");
    const [signerAddress, setSignerAddress] = useState("");

    // Initialize Ethereum provider and contract
    const initializeProvider = async () => {
        if (window.ethereum) {
            try {
                const provider = new ethers.providers.Web3Provider(window.ethereum);
                await provider.send("eth_requestAccounts", []); // Request access to user's accounts
                const signer = provider.getSigner();
                const contract = new ethers.Contract(VotingAddress, VotingAbi, signer);
                const signerAddress = await signer.getAddress();
                setSignerAddress(signerAddress);
                return { provider, signer, contract };
            } catch (err) {
                console.error("MetaMask connection failed:", err);
                setError("Failed to connect to MetaMask. Please make sure MetaMask is unlocked.");
            }
        } else {
            setError("MetaMask is not installed.");
        }
    };

    // Fetch election details
    const fetchElectionDetails = async () => {
        try {
            const { contract } = await initializeProvider();
            const election = await contract.getElection(id);
            const fetchedCandidates = await contract.getCandidates(id);

            const formattedCandidates = fetchedCandidates.map((candidate) => ({
                name: candidate.name,
                voteCount: candidate.voteCount.toString(),
            }));

            setCandidates(formattedCandidates);

            // Get current time in UNIX timestamp (in seconds)
            const currentTime = Math.floor(Date.now() / 1000); 

            if (currentTime < election.startTime) {
                setElectionStatus("Election has not started yet.");
                setCanVote(false);
            } else if (currentTime > election.endTime) {
                setElectionStatus("Election has ended.");
                setCanVote(false);
            } else {
                setElectionStatus("Election is ongoing.");
                setCanVote(true);
            }
        } catch (err) {
            console.error("Error fetching election details:", err);
            setError("Failed to load election details.");
        }
    };

    // Handle voting
    const vote = async (electionId, candidateIndex) => {
        try {
            const canVote = await checkElectionStatus(electionId);
            if (!canVote) return;
    
            const provider = new ethers.providers.Web3Provider(window.ethereum);
            const signer = provider.getSigner();
            const contract = new ethers.Contract(VotingAddress, VotingAbi, signer);
    
            const tx = await contract.vote(electionId, candidateIndex);
            await tx.wait();
    
            console.log("Vote successfully submitted!");
            fetchCandidates();
        } catch (err) {
            console.error("Error during voting:", err);
            setError("Failed to submit your vote.");
        }
    };
    

    // Fetch election details when component mounts
    useEffect(() => {
        fetchElectionDetails();
    }, [id]);

    return (
        <div>
            <h1>Election {id}</h1>
            {error && <p style={{ color: "red" }}>{error}</p>}
            <p>{electionStatus}</p>
            {canVote ? (
                <ul>
                    {candidates.map((candidate, index) => (
                        <li key={index}>
                            {candidate.name}: {candidate.voteCount} votes
                            <Button
                                variant="contained"
                                color="primary"
                                onClick={() => vote(index)}
                                style={{ marginLeft: "10px" }}
                            >
                                Vote
                            </Button>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>You cannot vote in this election.</p>
            )}
        </div>
    );
}

export default VotingPage;
