import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import { VotingAddress, VotingAbi } from "../context/constant";
import { Button } from "@mui/material";
import { useParams } from "react-router-dom";

function VotingPage() {
    const { id } = useParams(); // Get election ID from URL
    const [candidates, setCandidates] = useState([]);
    const [error, setError] = useState(null);
    const [electionStatus, setElectionStatus] = useState("");
    const [canVote, setCanVote] = useState(false);
    const [hasVoted, setHasVoted] = useState(false);
    const [signerAddress, setSignerAddress] = useState("");

    // Initialize Web3Provider and connect to MetaMask
    const initializeProvider = async () => {
        try {
            console.log("Initializing provider...");
            const provider = new ethers.providers.Web3Provider(window.ethereum, "any");
            await provider.send("eth_requestAccounts", []); // Request accounts permission
            const signer = provider.getSigner();
            const contract = new ethers.Contract(VotingAddress, VotingAbi, signer);

            // Get the current account address
            const currentSignerAddress = await signer.getAddress();
            setSignerAddress(currentSignerAddress);
            console.log("Using account:", currentSignerAddress);

            // Listen for account changes and update signerAddress accordingly
            window.ethereum.on("accountsChanged", (accounts) => {
                const newSignerAddress = accounts[0];
                setSignerAddress(newSignerAddress);
                console.log("Switched to account:", newSignerAddress);
            });

            return { provider, signer, contract };
        } catch (err) {
            setError("Failed to initialize provider.");
            console.error("Error initializing provider:", err);
        }
    };

    // Fetch election details (including candidates)
    const fetchElectionDetails = async () => {
        try {
            const { contract } = await initializeProvider();

            // Fetch election data
            const election = await contract.getElection(id);
            const candidatesData = await contract.getCandidates(id);

            const formattedCandidates = candidatesData.map((candidate) => ({
                name: candidate.name,
                voteCount: candidate.voteCount.toString(),
            }));

            setCandidates(formattedCandidates);

            // Check if the election is still active and if the user has voted
            const currentTime = Math.floor(Date.now() / 1000); // Current time in seconds

            if (currentTime < election.startTime.toNumber()) {
                setElectionStatus("Election has not started yet.");
                setCanVote(false);
            } else if (currentTime > election.endTime.toNumber()) {
                setElectionStatus("Election has ended.");
                setCanVote(false);
            } else {
                setElectionStatus("Election is ongoing.");
                setCanVote(true);
            }

            // Check if the user has already voted
            const voted = await contract.hasVoted(id, signerAddress);
            setHasVoted(voted);

        } catch (err) {
            console.error("Error fetching election details:", err);
            setError("Failed to load election details.");
        }
    };

    // Handle voting for a candidate
    const handleVote = async (candidateIndex) => {
        try {
            const { contract } = await initializeProvider();

            if (hasVoted) {
                setError("You have already voted in this election.");
                return;
            }

            // Call the vote function in the contract
            const tx = await contract.vote(id, candidateIndex, {
                gasLimit: 3000000, // Set the gas limit (can be adjusted)
            });

            // Wait for the transaction to be mined
            const receipt = await tx.wait();
            console.log("Vote transaction hash:", receipt.transactionHash);

            setError("Vote successfully cast!");

            // Refresh election details after voting
            fetchElectionDetails();
        } catch (err) {
            console.error("Error during voting:", err);
            setError("An unexpected error occurred. Please try again.");
        }
    };

    // Fetch election details when the component mounts or election ID changes
    useEffect(() => {
        fetchElectionDetails();
    }, [id]);

    return (
        <div>
            <h1>Election {id}</h1>
            {error && <p style={{ color: "red" }}>{error}</p>}
            <p>{electionStatus}</p>
            {canVote ? (
                <div>
                    <h3>Candidates</h3>
                    <ul>
                        {candidates.map((candidate, index) => (
                            <li key={index}>
                                {candidate.name}: {candidate.voteCount} votes
                                <Button
                                    variant="contained"
                                    color="primary"
                                    onClick={() => handleVote(index)}
                                    style={{ marginLeft: "10px" }}
                                >
                                    Vote
                                </Button>
                            </li>
                        ))}
                    </ul>
                </div>
            ) : (
                <p>{hasVoted ? "You have already voted." : "You cannot vote in this election."}</p>
            )}
            <Button
                variant="contained"
                color="secondary"
                onClick={fetchElectionDetails}
                style={{ marginTop: "10px" }}
            >
                Refresh Election Details
            </Button>
        </div>
    );
}

export default VotingPage;
