import React, { useEffect, useState } from "react";
import { ethers } from "ethers";
import { VotingAddress, VotingAbi } from "./context/constant";

function App() {
    const [candidates, setCandidates] = useState([]);
    const [remainingTime, setRemainingTime] = useState("");
    const [error, setError] = useState(null);

    const fetchENSData = async () => {
        const provider = new ethers.providers.JsonRpcProvider("http://127.0.0.1:8545", {
            name: "hardhat",
            chainId: 31337,
        });

        const network = await provider.getNetwork();
        console.log("Connected network:", network);



        const ensAddress = "0x00000000000C2E074eC69A0dFb2997BA6C7d2e1e"; // ENS Registry
        const ensABI = [
            "function resolver(bytes32 node) external view returns (address)"
        ];
        const namehash = ethers.utils.namehash("vitalik.eth");

        const ens = new ethers.Contract(ensAddress, ensABI, provider);
        const resolverAddress = await ens.resolver(namehash);
        console.log("Resolver address:", resolverAddress);

        const resolverABI = [
            "function addr(bytes32 node) external view returns (address)"
        ];
        const resolver = new ethers.Contract(resolverAddress, resolverABI, provider);
        const address = await resolver.addr(namehash);
    };

    useEffect(() => {
        const fetchCandidates = async () => {
            try {
                console.log("Starting fetchCandidates...");

                // Explicitly set the provider network to avoid ENS issues
                const provider = new ethers.providers.JsonRpcProvider("http://127.0.0.1:8545", {
                    name: "hardhat",
                    chainId: 31337,
                });
                console.log("Provider initialized:", provider);

                // Initialize signer
                const signer = provider.getSigner();
                const signerAddress = await signer.getAddress();
                console.log("Signer address:", signerAddress);

                // Initialize contract
                const contract = new ethers.Contract(VotingAddress, VotingAbi, signer);
                console.log("Contract instance:", contract);

                // Fetch candidates
                const candidates = await contract.getAllVotesOfCandidates();
                console.log("Fetched candidates (raw):", candidates);

                const remainingTime = await contract.getRemainingTime();
                console.log("remainingTime:", remainingTime);

                // Format candidates
                const formattedCandidates = candidates.map((candidate) => ({
                    name: candidate.name,
                    voteCount: candidate.voteCount.toString(),
                }));

                setCandidates(formattedCandidates);
                console.log("Formatted candidates:", formattedCandidates);
            } catch (error) {
                console.error("Error in fetchCandidates:", error);
                setError(error.message);
            }
        };




        fetchCandidates();
        fetchENSData();
    }, []);





    return (
        <div>
            <h1>Voting App</h1>
            <ul>
                {candidates.map((candidate, index) => (
                    <li key={index}>
                        {candidate.name}: {candidate.voteCount} votes
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default App;
