import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { ethers } from "ethers";
import { VotingAddress, VotingAbi } from "../context/constant";
import { Button } from "@mui/material";

function VotingPage() {
    const { id } = useParams();
    const [candidates, setCandidates] = useState([]);
    const [error, setError] = useState(null);

    const fetchCandidates = async () => {
        try {
            const provider = new ethers.providers.JsonRpcProvider("http://127.0.0.1:8545", {
                name: "hardhat",
                chainId: 31337,
            });

            const signer = provider.getSigner();
            const contract = new ethers.Contract(VotingAddress, VotingAbi, signer);

            const fetchedCandidates = await contract.getCandidates(id);

            const formattedCandidates = fetchedCandidates.map((candidate) => ({
                name: candidate.name,
                voteCount: candidate.voteCount.toString(),
            }));

            setCandidates(formattedCandidates);
        } catch (err) {
            console.error("Error fetching candidates:", err);
            setError("Failed to load candidates.");
        }
    };

    const handleVote = async (candidateIndex) => {
        try {
            const provider = new ethers.providers.JsonRpcProvider("http://127.0.0.1:8545", {
                name: "hardhat",
                chainId: 31337,
            });            const signer = provider.getSigner();
            const contract = new ethers.Contract(VotingAddress, VotingAbi, signer);

            const tx = await contract.vote(id, candidateIndex);
            await tx.wait();
            fetchCandidates(); // Refresh candidates list
        } catch (err) {
            console.error("Error voting:", err);
            setError("Failed to submit your vote.");
        }
    };

    useEffect(() => {
        fetchCandidates();
    }, [id]);

    return (
        <div>
            <h1>Election {id}</h1>
            {error && <p style={{ color: "red" }}>{error}</p>}
            <ul>
                {candidates.map((candidate, index) => (
                    <li key={index}>
                        {candidate.name}: {candidate.voteCount} votes
                        <Button onClick={() => handleVote(index)}>Vote</Button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default VotingPage;
