import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { ethers } from "ethers";
import { VotingAddress, VotingAbi } from "../context/constant";
import { Button } from "@mui/material";

function VotingPage() {
    const { id } = useParams();
    const [candidates, setCandidates] = useState([]);
    const [error, setError] = useState(null);
    const [canVote, setCanVote] = useState(false); // Change initial state to false for better control
    const [electionStatus, setElectionStatus] = useState("");
    const [signerAddress, setSignerAddress] = useState("");

    // Initialize Ethereum provider and signer
    const initializeProvider = async () => {
        try {
            const provider = new ethers.providers.Web3Provider(window.ethereum);
            await provider.send("eth_requestAccounts", []); // Request account access
            const signer = provider.getSigner();
            const contract = new ethers.Contract(VotingAddress, VotingAbi, signer);

            const signerAddress = await signer.getAddress();
            setSignerAddress(signerAddress);

            // Recheck voting status when the account changes
            const hasVoted = await contract.hasVoted(id, signerAddress);
            if (hasVoted) {
                setError("You have already voted in this election.");
                setCanVote(false);  // Disable the voting button if already voted
            } else {
                setCanVote(true);   // Allow voting if user hasn't voted
            }

            return { provider, signer, contract };
        } catch (err) {
            setError("Failed to initialize provider.");
            console.error("Error initializing provider:", err);
        }
    };

    // Fetch election details
    const fetchElectionDetails = async () => {
        try {
            const { contract } = await initializeProvider();
            const election = await contract.getElection(id);
            const fetchedCandidates = await contract.getCandidates(id);

            const formattedCandidates = fetchedCandidates.map((candidate) => ({
                name: candidate.name,
                voteCount: candidate.voteCount.toString(),
            }));

            setCandidates(formattedCandidates);

            // Get current time in UNIX timestamp (in seconds)
            const currentTime = Math.floor(Date.now() / 1000); // Current time in seconds

            if (currentTime < election.startTime) {
                setElectionStatus("Election has not started yet.");
                setCanVote(false); // Prevent voting if election has not started
            } else if (currentTime > election.endTime) {
                setElectionStatus("Election has ended.");
                setCanVote(false); // Prevent voting if election has ended
            } else {
                setElectionStatus("Election is ongoing.");
                setCanVote(true); // Allow voting if election is ongoing
            }
        } catch (err) {
            console.error("Error fetching election details:", err);
            setError("Failed to load election details.");
        }
    };

    // Voting function with added checks and gas estimation
    const vote = async (candidateIndex) => {
        try {
            const { contract } = await initializeProvider();

            // Recheck if the current account has voted
            const hasVoted = await contract.hasVoted(id, signerAddress);
            if (hasVoted) {
                setError("You have already voted in this election.");
                setCanVote(false);
                return;
            }

            // Ensure candidate index is valid
            if (candidateIndex < 0 || candidateIndex >= candidates.length) {
                setError("Invalid candidate index.");
                return;
            }

            // Log the current election details to make sure the state is correct
            const election = await contract.getElection(id);
            const currentTime = Math.floor(Date.now() / 1000); // Current UNIX timestamp
            if (currentTime < election.startTime || currentTime > election.endTime) {
                setError("Election is not in valid voting period.");
                return;
            }

            // Estimate gas
            let gasEstimate;
            try {
                gasEstimate = await contract.estimateGas.vote(id, candidateIndex);
            } catch (gasErr) {
                console.error("Gas estimation failed, using fallback gas limit", gasErr);
                gasEstimate = 1000000;  // Fallback gas limit
            }

            // Send the transaction and get the transaction hash
            const tx = await contract.vote(id, candidateIndex, { gasLimit: gasEstimate });

            // Once the transaction is sent, wait for confirmation
            const receipt = await tx.wait();

            // The transaction hash is available from the receipt
            console.log("Transaction Hash:", receipt.transactionHash);
            setError("Successfully voted!");

            // Refresh election details
            fetchElectionDetails();  // Refresh after voting
        } catch (err) {
            console.error("Error during voting:", err);
            if (err.code === -32603) {
                // Log revert reason if available
                if (err.data && err.data.message) {
                    setError("Failed to submit your vote. See console for details.");
                }
            } else {
                setError("An unexpected error occurred. Please try again.");
            }
        }
    };

    // Listen for account changes (MetaMask switch)
    useEffect(() => {
        const handleAccountChange = async (accounts) => {
            if (accounts.length > 0) {
                setSignerAddress(accounts[0]);
                fetchElectionDetails();  // Re-fetch election details when account changes
            }
        };

        // Set listener for account change
        window.ethereum.on('accountsChanged', handleAccountChange);

        // Fetch election details when the component mounts or id changes
        fetchElectionDetails();

        // Cleanup listener when the component unmounts
        return () => {
            window.ethereum.removeListener('accountsChanged', handleAccountChange); // Cleanup listener correctly
        };
    }, [id]);

    return (
        <div>
            <h1>Election {id}</h1>
            {error && <p style={{ color: "red" }}>{error}</p>}
            <p>{electionStatus}</p>
            {canVote ? (
                <ul>
                    {candidates.map((candidate, index) => (
                        <li key={index}>
                            {candidate.name}: {candidate.voteCount} votes
                            <Button
                                variant="contained"
                                color="primary"
                                onClick={() => vote(index)}
                                style={{ marginLeft: "10px" }}
                            >
                                Vote
                            </Button>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>You cannot vote in this election.</p>
            )}
        </div>
    );
}

export default VotingPage;
