import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { ethers } from "ethers";
import { VotingAddress, VotingAbi } from "../context/constant";
import { Button } from "@mui/material";

function VotingPage() {
    const { id } = useParams();
    const [candidates, setCandidates] = useState([]);
    const [error, setError] = useState(null);
    const [canVote, setCanVote] = useState(true);

    // Initialize provider and signer
    const initializeProvider = async () => {
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        await provider.send("eth_requestAccounts", []);
        const signer = provider.getSigner();
        const contract = new ethers.Contract(VotingAddress, VotingAbi, signer);

        return { provider, signer, contract };
    };

    const checkVoterStatus = async () => {
        try {
            const { signer, contract } = await initializeProvider();
            const voterAddress = await signer.getAddress();
    
            // Check if the voter has already voted in the election
            const hasVoted = await contract.c(voterAddress);
            setCanVote(!hasVoted); // Set to false if voter has already voted
        } catch (err) {
            console.error("Error checking voter status:", err);
        }
    };
    
    

    const fetchCandidates = async () => {
        try {
            const { contract } = await initializeProvider();
            const fetchedCandidates = await contract.getCandidates(id);

            const formattedCandidates = fetchedCandidates.map((candidate) => ({
                name: candidate.name,
                voteCount: candidate.voteCount.toString(),
            }));

            setCandidates(formattedCandidates);
        } catch (err) {
            console.error("Error fetching candidates:", err);
            setError("Failed to load candidates.");
        }
    };

    const checkCanVote = async () => {
        try {
            const { signer, contract } = await initializeProvider();
            const voterAddress = await signer.getAddress();
            const voteStatus = await contract.voters(voterAddress);

            setCanVote(!voteStatus);
        } catch (err) {
            console.error("Error checking vote status:", err);
        }
    };

    const isElectionActive = async () => {
        const { contract } = await initializeProvider();
        const election = await contract.getElection(id);
    
        const currentTime = Math.floor(Date.now() / 1000);
        if (currentTime < election.startTime) {
            setError("Election has not started yet.");
            return false;
        }
        if (currentTime > election.endTime) {
            setError("Election has already ended.");
            return false;
        }
        return true;
    };

    const fetchElectionStatus = async () => {
        try {
            const { contract } = await initializeProvider();
            const election = await contract.getElection(id);
            const currentTime = Math.floor(Date.now() / 1000);
    
            if (currentTime < election.startTime) {
                setError("Election has not started yet.");
            } else if (currentTime > election.endTime) {
                setError("Election has already ended.");
            } else {
                setError(null);
            }
        } catch (err) {
            console.error("Error fetching election status:", err);
        }
    };
    
    

    const vote = async (candidateIndex) => {
        try {
            setError(null);
    
            // Check if election is active
            const active = await isElectionActive();
            if (!active) return;
    
            const { contract } = await initializeProvider();
    
            // Submit vote
            const tx = await contract.vote(id, candidateIndex);
            await tx.wait();
    
            console.log(`Successfully voted for candidate index ${candidateIndex}.`);
    
            // Refresh data
            fetchCandidates();
            checkCanVote();
        } catch (err) {
            console.error("Error during voting:", err);
            setError("Failed to submit your vote.");
        }
    };
    
    
    useEffect(() => {
        fetchCandidates();
        checkCanVote();
        fetchElectionStatus();
        checkVoterStatus();
    }, [id]);

    return (
        <div>
            <h1>Election {id}</h1>
            {error && <p style={{ color: "red" }}>{error}</p>}
            {canVote ? (
                <ul>
                    {candidates.map((candidate, index) => (
                        <li key={index}>
                            {candidate.name}: {candidate.voteCount} votes
                            <Button
                                variant="contained"
                                color="primary"
                                onClick={() => vote(index)}
                                style={{ marginLeft: "10px" }}
                            >
                                Vote
                            </Button>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>You have already voted.</p>
            )}
        </div>
    );
}

export default VotingPage;
