import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { ethers } from "ethers";
import { VotingAddress, VotingAbi } from "../context/constant";
import { Button } from "@mui/material";

function VotingPage() {
    const { id } = useParams();
    const [candidates, setCandidates] = useState([]);
    const [error, setError] = useState(null);
    const [canVote, setCanVote] = useState(false);
    const [electionStatus, setElectionStatus] = useState("");
    const [signerAddress, setSignerAddress] = useState("");

    // Use Web3Provider to connect with MetaMask
    const initializeProvider = async () => {
        try {
            console.log("Initializing provider...");
            const provider = new ethers.providers.Web3Provider(window.ethereum, "any");
            await provider.send("eth_requestAccounts", []); // Request accounts permission
            const signer = provider.getSigner();
            const contract = new ethers.Contract(VotingAddress, VotingAbi, signer);

            const currentSignerAddress = await signer.getAddress();
            setSignerAddress(currentSignerAddress);
            console.log("Using account:", currentSignerAddress);

            // Listen for account changes and update signerAddress accordingly
            window.ethereum.on("accountsChanged", (accounts) => {
                const newSignerAddress = accounts[0];
                setSignerAddress(newSignerAddress);
                console.log("Switched to account:", newSignerAddress);
            });

            return { provider, signer, contract };
        } catch (err) {
            setError("Failed to initialize provider.");
            console.error("Error initializing provider:", err);
        }
    };

    const handleVote = async (candidateIndex) => {
        try {
            const { contract } = await initializeProvider();
            console.log("Using account:", signerAddress);
            const hasVoted = await contract.hasVoted(id, signerAddress);
            console.log("Has the current account voted?", hasVoted);
    
            if (hasVoted) {
                setError("You have already voted in this election.");
                setCanVote(false);
                return;
            }
    
            const election = await contract.getElection(id);
            const currentTime = Math.floor(Date.now() / 1000); // Current time in UNIX timestamp
            console.log("Current time:", currentTime);
            console.log("Election start time:", election.startTime.toString());
    
            if (currentTime < election.startTime.toNumber() || currentTime > election.endTime.toNumber()) {
                setError("Election is not in valid voting period.");
                return;
            }
    
            let gasEstimate;
            try {
                gasEstimate = await contract.estimateGas.vote(id, candidateIndex);
                console.log("Gas Estimate:", gasEstimate.toString());
            } catch (gasErr) {
                console.error("Gas estimation failed:", gasErr);
                gasEstimate = ethers.BigNumber.from(30000000); // Fallback gas limit
            }
    
            const tx = await contract.vote(id, candidateIndex, { gasLimit: gasEstimate });
            const receipt = await tx.wait();
            console.log("Transaction Hash:", receipt.transactionHash);
            setError("Successfully voted!");
            fetchElectionDetails(); // Refresh election details after voting
    
        } catch (err) {
            console.error("Error during voting:", err);
            setError("An unexpected error occurred. Please try again.");
        }
    };
    

    // Fetch election details
    const fetchElectionDetails = async () => {
        try {
            const { contract } = await initializeProvider();
            const election = await contract.getElection(id);
            const fetchedCandidates = await contract.getCandidates(id);

            const formattedCandidates = fetchedCandidates.map((candidate) => ({
                name: candidate.name,
                voteCount: candidate.voteCount.toString(),
            }));

            setCandidates(formattedCandidates);

            const currentTime = Math.floor(Date.now() / 1000); // Get current time in seconds
            console.log("Current time (UNIX timestamp):", currentTime);
            console.log("Election start time (UNIX timestamp):", election.startTime.toString());
            console.log("Election end time (UNIX timestamp):", election.endTime.toString());

            // Ensure that times are correctly handled as seconds
            if (currentTime < election.startTime.toNumber()) {
                setElectionStatus("Election has not started yet.");
                setCanVote(false);
            } else if (currentTime > election.endTime.toNumber()) {
                setElectionStatus("Election has ended.");
                setCanVote(false);
            } else {
                setElectionStatus("Election is ongoing.");
                setCanVote(true);
            }
        } catch (err) {
            console.error("Error fetching election details:", err);
            setError("Failed to load election details.");
        }
    };

    // Voting function
    const vote = async (candidateIndex) => {
        try {
            const { contract } = await initializeProvider();

            const hasVoted = await contract.hasVoted(id, signerAddress);
            console.log("Has the current account voted?", hasVoted);
            if (hasVoted) {
                setError("You have already voted in this election.");
                setCanVote(false);
                return;
            }

            if (candidateIndex < 0 || candidateIndex >= candidates.length) {
                setError("Invalid candidate index.");
                return;
            }

            const election = await contract.getElection(id);
            const currentTime = Math.floor(Date.now() / 1000); // Get current time in seconds
            console.log("Current time (UNIX timestamp):", currentTime);
            console.log("Election start time (UNIX timestamp):", election.startTime.toNumber());
            console.log("Election end time (UNIX timestamp):", election.endTime.toNumber());

            // Ensure the election start time and current time are compared correctly
            if (currentTime < election.startTime.toNumber() || currentTime > election.endTime.toNumber()) {
                setError("Election is not in valid voting period.");
                return;
            }

            // Send the transaction and get the transaction hash
            const tx = await contract.vote(id, candidateIndex, {
                gasLimit: 3000000,  // Fallback gas limit (MetaMask will handle this)
            });

            // Wait for transaction to be mined
            const receipt = await tx.wait();
            console.log("Transaction Hash:", receipt.transactionHash);
            setError("Successfully voted!");

            fetchElectionDetails();  // Refresh election details after voting
        } catch (err) {
            console.error("Error during voting:", err);
            setError("An unexpected error occurred. Please try again.");
        }
    };

    // Fetch election details when the component mounts or election id changes
    useEffect(() => {
        fetchElectionDetails();
    }, [id]);

    return (
        <div>
            <h1>Election {id}</h1>
            {error && <p style={{ color: "red" }}>{error}</p>}
            <p>{electionStatus}</p>
            {canVote ? (
                <ul>
                    {candidates.map((candidate, index) => (
                        <li key={index}>
                            {candidate.name}: {candidate.voteCount} votes
                            <Button
                                variant="contained"
                                color="primary"
                                onClick={() => handleVote(index)}
                                style={{ marginLeft: "10px" }}
                            >
                                Vote
                            </Button>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>You cannot vote in this election.</p>
            )}
        </div>
    );
}

export default VotingPage;
